import React, { useState } from 'react'
import {
  FaFacebook,
  FaTwitter,
  FaInstagram,
  FaYoutube,
  FaPinterest
} from 'react-icons/fa'
import { Link } from 'react-router-dom'

import './Footer.scss'

import data from './data.json'

const FooterInnerNavItemBtn = ({ item }) => {
  const [visible, setVisible] = useState(false)

  return (
    <li className='footer-inner-nav-item' onClick={() => setVisible(!visible)}>
      {item.subMenu ? (
        <>
          <button
            role='heading'
            aria-level='1'
            className={
              visible
                ? 'footer-inner-nav-item-btn footer-inner-nav-item-btn_expandable footer-inner-nav-item-btn_isOpen'
                : 'footer-inner-nav-item-btn footer-inner-nav-item-btn_expandable'
            }
          >
            {item.menu}
          </button>
          <ul
            className={
              visible
                ? 'footer-inner-nav-item-expanded_isOpen'
                : 'footer-inner-nav-item-expanded'
            }
          >
            {item.subMenu.map(({ name, path }, i) =>
              path ? (
                <li key={i}>
                  <Link to={path}>{name}</Link>
                </li>
              ) : (
                <li key={i}>{name}</li>
              )
            )}
          </ul>
        </>
      ) : (
        <Link to={item.path} className='footer-inner-nav-item-btn'>
          {item.menu}
        </Link>
      )}
    </li>
  )
}

